var radio = require('nrf').connect('/dev/spidev0.0', 24);
radio.channel(10).dataRate('250kbps').crcBytes(2).autoRetransmit({count:15, delay:4000});
radio.begin(function () {
    var rx = radio.openPipe('rx', 1034834473170),
        tx = radio.openPipe('tx', 1034834473185);
	//tx.write(0x1222);
    //rx.pipe(tx);        // echo back everything
	rx.on('data', function (d) {
            console.log("Got data, will respond", d.readUInt32BE(0));
	    console.log(d);
            tx.write(d);
        });
});
radio.printDetails();



